  }else {
    setBrand(
      brandFilters.filter((elem))
    )
  }
}
const categoryFilter = (event) => {
  if (event.target.checked) {
    setCategory([...categoryItems, event.target.value]);
  } else {
    setCategory(
      categoryItems.filter((elem) => elem !== event.target.value)
    );
  }
};

