    var catItems = [];
    items.forEach((cate) => {
      catItems.push(cate.category);
    });
    var categoryItem = new Set(catItems);
    var catArr = [];
    for (let j of categoryItem) {
      catArr.push(j);
    }
    // <---------------------This Area is Show brands----------------------------->
    var barItems = [];
    items.forEach((cate) => {
      barItems.push(cate.brand);
    });
    var brandItem = new Set(barItems);
    var brandArr = [];
    for (let j of brandItem) {
      brandArr.push(j);
    }
    // <-----------filters-------------------------------------------------------->
    return (
      <>
        <Navbar onclick={searchFilter}/>
        <div className="parent">
          <div className="main">
            <div className="genderclass">
              <h4>FILTERS</h4>
              {genArr.map((val) => {
                return (
                  <Radio gender={val} value={val} onClick={genderFilter} />
                );
              })}
              <hr />
              <h4>Categories</h4>
              {catArr.map((elem) => {
                return <Checkbox value={elem} onClick={categoryFilter}/>;
              })}
              <hr />
              <h4>Brand</h4>
              {brandArr.map((elm) => {
                return <Brand value={elm} />;
              })}
            </div>
            {/* <---Cards-----------------> */}
            <div className="Cards">
              <div className="cardclass">
                {allData().map((para,k) => {
                  return (
                    <Cards
                      key={k}
                      img={para.searchImage}
                      brand={para.brand}
                      product={para.product}
                      price={para.price}
                      mrp={para.mrp}
                    />
                  );
                })}
              </div>
            </div>
          </div>
        </div>
      </>
    );
  };

export default App;
