import "./App.css";
import Navbar from "./Navbar";
import Cards from "./Components/Cards";
import Radio from "./Components/Radio";
import Checkbox from "./Components/Checkbox";
import Brand from "./Components/Brand";
import React, { useState, useEffect } from "react";
import "./Components/component.css";

function App() {
  const [items, myfun] = useState([]);
  const myApi = () => {
    fetch("https://run.mocky.io/v3/bf175661-5e9f-4112-8580-d587759ff72e")
      .then((apiData) => apiData.json())
      .then((data) => data.products)
      .then((data) => myfun(data));
  };
  useEffect(() => {
    myApi();
  }, []);
  // <----------------------------This Area is show Gender Items ------------------------------------------------->
  var genItem = [];
  items.forEach((gender) => {
    genItem.push(gender.gender);
  });
  var genItem1 = new Set(genItem);
  var genArr = [];
  for (let i of genItem1) {
    genArr.push(i);
  }
  // <----------------------------This Area is Show Category--------------------------------------------------------->
  var catItems = [];
  items.forEach((cate) => {
    catItems.push(cate.category);
  });
  var categoryItem = new Set(catItems);
  var catArr = [];
  for (let j of categoryItem) {
    catArr.push(j);
  }
  // <---------------------This Area is Show brands----------------------------->
  var barItems = [];
  items.forEach((cate) => {
    barItems.push(cate.brand);
  });
  var brandItem = new Set(barItems);
  var brandArr = [];
  for (let j of brandItem) {
    brandArr.push(j);
  }
  return (
    <>
      <Navbar />
      <div className="container-fluid">
        <div className="row  ml-2 p-5">
          <div className="ol-md-3 border border-secondry">
            <div className="container mt-3">


            </div>
          </div>
        </div>

      <div className="main">
        <div className="genderclass">
          <h4>FILTERS</h4>
          {genArr.map((val) => {
            return <Radio gender={val} value={val} />;
          })}
          <hr />
          <h4>Categories</h4>
          {catArr.map((elem) => {
            return <Checkbox value={elem} />;
          })}
          <hr />
          <h4>Brand</h4>
          {brandArr.map((el) => {
            return <Brand value={el} />;
          })}
        </div>
        {/* Cards----------------- */}
        <div className="Cards">
          <div className="cardclass">
            {items.map((para) => {
              return (
                <Cards
                  img={para.searchImage}
                  brand={para.brand}
                  product={para.product}
                  price={para.price}
                  mrp={para.mrp}
                />
              );
            })}
          </div>
        </div>
      </div>
      </div>

{/* =======*/}
<div className="container-fluid">
        <div className="row  ml-2 p-5">
          <div className="col-md-3 border border-secondry">
            <div className="container mt-3">
              <div>
                <h1>Filter</h1>
                <hr />
                {RadioArr.map((Elem) => {
                  return (
                    <Radio gender={Elem} value={Elem} onClick={genderFilter} />
                  );
                })}
              </div>
              <div>
                <h1>Category</h1>
                {CheckboxArr.map((Elem) => {
                  return (
                    <Checkbox
                      category={Elem}
                      value={Elem}
                      onClick={categoryFilter}
                    />
                  );
                })}
              </div>
            </div>
          </div>
          <div class="col-md-9">
            <div className="container">
            <div className="row mt-2">
              {filteredData().map((data, i) => {
                return (
                  <ProductCard
                    key={i}
                    images={data.searchImage}
                    brand={data.brand}
                    product={data.additionalInfo}
                    discountedPrice={data.price}
                    price={data.mrp}
                    discount={data.discountDisplayLabel}
                  />
                );
              })}
            </div>
            </div>
          </div>
        </div>
      </div>





    </>
  );
}
export default App;
